public class ContactInfo {    private String email;    private String address;    private double phoneNumber;    public ContactInfo(String email, String address, double phoneNumber){        this.email = email;        this.address = address;        this.phoneNumber = phoneNumber;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    public double getPhoneNumber() {        return phoneNumber;    }    public void setPhoneNumber(double phoneNumber) {        this.phoneNumber = phoneNumber;    }}